import React, { useState } from "react";
import "./login.css";
import { Link } from "react-router-dom";
import { userAuth } from "../Hooks/userAuth";
const Login = () => {
  let [email, setEmail] = useState("");
  let [password, setPassword] = useState("");
  let [name, setName] = useState("");
  let { userLogin } = userAuth();

  let handleSubmit = function (e) {
    e.preventDefault();
    let mainEmail = email.trim();
    let mainPassword = password.trim();
    userLogin(mainEmail, mainPassword);
    console.log();
  };

  return (
    <React.Fragment>
      <div className="main-div">
        <div className="input-div">
          <form className="form" onSubmit={handleSubmit}>
            <h1 id="login">Login</h1>
            <input
              id="name"
              type="text"
              name="name"
              placeholder="Name"
              value={name}
              onChange={(e) => {
                setName(e.target.value);
              }}
            ></input>
            <input
              id="name"
              type="email"
              name="email"
              placeholder="Email"
              value={email}
              onChange={(e) => {
                setEmail(e.target.value);
              }}
            ></input>
            <input
              id="name"
              type="password"
              name="password"
              placeholder="Password"
              value={password}
              onChange={(e) => {
                setPassword(e.target.value);
              }}
            ></input>

            <input id="submit" type="submit" value="Login"></input>

            <p id="or">OR</p>
            <Link to="/forgot-password" id="forgot">
              Forgot password?
            </Link>
            <div className="remember-div">
              <input id="Remember" type="checkbox"></input>
              <label htmlFor="Remember"> Remember me</label>
            </div>
          </form>
        </div>
      </div>
    </React.Fragment>
  );
};

export default Login;
